import {useState} from 'react'

const App = () => {
  const [person, setPerson] = useState([
    {
      name: 'toto le toto',
      num: '23215'
    }
  ])
  const [newName, setNewName] = useState('')
  const [newNum, setNewNum] = useState('')

  const HandleNameChange = (event) => {
    setNewName(event.target.value)
    setNewNum(event.target.val)
  }

  function checkName (name) {
    const a = Object.getOwnPropertyNames(name.name)
    const b = Object.getOwnPropertyNames(newName)

    if (a.length !== b.length) return false
    const hasSameKey = a.every(value => !!b.find(v => v === value))
    if (!hasSameKey) return false

    for (const key of a) if (a[key] !== b[key]) return false

    return true
  }

  const AddName = (event) => {
    event.preventDefault()
    const personObj = {
      name: newName,
    }
    if (person.find(checkName)) {
      alert(`${newName} already add to phonebook`)
      return
    }
    setPerson(person.concat(personObj))
    setNewName('')
  }

  return (
    <div>
      <h2>phonebook</h2>
      <form onSubmit={AddName}>
        <div>
          name: <input value={newName} onChange={HandleNameChange}/>
        </div>
        <div>
          num: <input val={newNum} onChange={HandleNameChange}/>
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>numbers</h2>
        {person.map((al, i) =>
            <li key={i}>{al.name}</li>
        )}
    </div>
  )
}

export default App
