const Header = (props) => {
  return (
    <h1>{props.course}</h1>
  )
}

const Part = (props) => {
  return (
    <p>{props.part} {props.exercises}</p>
  )
}

const Content = (props) => {
  const parts = props.part

  return (
    <div>
      {parts.map(parts =>
        <Part key={parts.id} part={parts.name} exercises={parts.exercises}/>
      )}
    </div>
  )
}

const Total = (props) => {
  return (
    <p>Number of exercises {props.count[0].exercises + props.count[1].exercises + props.count[2].exercises}</p>
  )
}

const Course = (props) => {
  const course = props.course
  return (
    <div>
      {course.map(course =>
      <div>
        <Header key={course.id} course={course.name}/>
        <Content part={course.parts}/>
      </div>  
      )}
      <Content part={course.parts}/>
    </div>
  )
}

const App = () => {
  const course = [
    {
      id: 1,
      name: 'half stack application dev',
      parts: [
        {
          name: 'fundamentals of react',
          exercises: 20,
          id: 1
        },
        {
          name: 'using props to pass data',
          exercises: 7,
          id: 2
        },
        {
          name: 'state of a compement',
          exercises: 14,
          id: 3
        }
      ],
    },
  ]

  return <Course course={course}/>
}

export default App
