import { useState, useEffect }from 'react'
import axios from 'axios'

//component//
const ListCountrie = (props) => {
  const countrie = props.countrie.filter(countrie => countrie.name.search(props.search) === 0)
    return (
    <ul>
        {countrie.map((al, i) =>
            <li key={i}>{al.name}: {al.num}</li>
        )}
    </ul>
  )
}

const Filter = (props) => {
  return (  
    <form>
      <div>
        search: <input value={props.newSearch} onChange={props.HandleSearchChange}/>
      </div>
    </form>
  )
}
//end component

const App = () => {
  const [countrie, setCountrie] = useState([])
  const [newSearch, setNewSearch] = useState('')

  //handle function //
  const HandleSearchChange = (event) => {
    setNewSearch(event.target.value)
  }

  //data fetch
  useEffect( () => {
    console.log('effect')
    axios.get('https://restcountries.com/v3.1/all').then(response => {
      console.log('promise fullfilled', response.data)
      setCountrie(response.data)
    })
  }, [])

  return (
    <div>
      <h2>search</h2>
        <Filter HandleSearchChange={HandleSearchChange} newSearch={newSearch}/>
      <h2>countrie</h2>
        <ListCountrie countrie={countrie} search={newSearch}/>
    </div>
  )
}

export default App
