import { useState, useEffect }from 'react'
import personService from './services/person'
import axios from 'axios'

//component//
const ListPerson = ({persons, removePerson, search}) => {
  const person = persons.filter(person => person.name.search(search) === 0)
    return (
    <ul>
        {person.map((al, i) =>
            <p key={i}>{al.name}: {al.number}  <button onClick={removePerson}>sda</button></p> 
        )}
    </ul>
  )
}

const Person = ({person, removePerson}) => {
  return (  
    <p key={i}>{al.name}: {al.number}  <button onClick={removePerson}>sda</button></p> 
  )
}

const FormPerson = (props) => {
  return (
    <form onSubmit={props.Addperson}>
      <div>
        name: <input value={props.Name} onChange={props.HandleNameChange}/>
      </div>
      <div>
        num: <input value={props.Num} onChange={props.HandleNumChange}/>
      </div>
      <div>
        <button type="submit">add</button>
      </div>
    </form>
  )
}

const Filter = (props) => {
  return (  
    <form>
      <div>
        search: <input value={props.newSearch} onChange={props.HandleSearchChange}/>
      </div>
    </form>
  )
}
//end component

const App = () => {
  const [person, setPerson] = useState([])
  const [newName, setNewName] = useState('')
  const [newNum, setNewNum] = useState('')
  const [newSearch, setNewSearch] = useState('')

  //handle function //
  const HandleNameChange = (event) => {
    setNewName(event.target.value)
  }

  const HandleSearchChange = (event) => {
    setNewSearch(event.target.value)
  }

  const HandleNumChange = (event) => {
    setNewNum(event.target.value)
  }

  function checkName (name) {
    const a = Object.getOwnPropertyNames(name.name)
    const b = Object.getOwnPropertyNames(newName)

    if (a.length !== b.length) return false
    const hasSameKey = a.every(value => !!b.find(v => v === value))
    if (!hasSameKey) return false
    for (const key of a)
    {
      console.log(name.name[key], newName[key])
      if (name.name[key] !== newName[key]) {
        return false
      }
    }
    return true
  }

  const AddPerson = (event) => {
    event.preventDefault()
    const personObj = {
      name: newName,
      number: newNum,
    }

    if (person.find(checkName)) {
      alert(`${newName} already add to phonebook`)
      setNewName('')
      setNewNum('')
      return
    }

    personService.create(personObj)
    .then(returnperson => {
      setNewName('')
      setNewNum('')
      setPerson(person.concat(returnperson))
    })
  }

  const removePerson = (id) => {
    personService.remove(id)
    .then( person => {
      setNewName('')
      setNewNum('')
      setPerson(... person)
    })
  }

  //data fetch
  useEffect( () => {
    console.log('effect')
    personService.getAll().then(initperson => {
      setPerson(initperson)
    })
  }, [])

  return (
    <div>
      <h2>search</h2>
        <Filter HandleSearchChange={HandleSearchChange} newSearch={newSearch}/>
      <h2>Add new</h2>
        <FormPerson  Addperson={AddPerson} name={newName} num={newNum} HandleNameChange={HandleNameChange} HandleNumChange={HandleNumChange}/>
      <h2>numbers</h2>
        <ListPerson persons={person} search={newSearch} removePerson={() => removePerson(person.id)}/>
    </div>
  )
}

export default App
