import {useState} from 'react'

const philterName = (props) => {
  const a = Object.getOwnPropertyNames(props.name)
  const b = Object.getOwnPropertyNames(props.search)

  if (a.length !== b.length) return false
  const hasSameKey = a.every(value => !!b.find(v => v === value))
  if (!hasSameKey) return false

  for (const key of a) if (a[key] !== b[key]) return false

  return true
}

const ListPerson = (props) => {
  const person = props.person.filter(philterName)
  return (
    <ul>
        {person.map((al, i) =>
            <li key={i}>{al.name}: {al.num}</li>
        )}
    </ul>
  )
}


const App = () => {
  const [person, setPerson] = useState([
    {
      name: 'toto le toto',
      num: '23215',
      show: true
    }
  ])
  const [newName, setNewName] = useState('')
  const [newNum, setNewNum] = useState('')
  const [newSearch, setNewSearch] = useState('')

  const HandleNameChange = (event) => {
    setNewName(event.target.value)
  }

  const HandleSearchChange = (event) => {
    setNewSearch(event.target.value)
  }

  const HandleNumChange = (event) => {
    setNewNum(event.target.value)
  }

  function checkName (name) {
    const a = Object.getOwnPropertyNames(name.name)
    const b = Object.getOwnPropertyNames(newName)

    if (a.length !== b.length) return false
    const hasSameKey = a.every(value => !!b.find(v => v === value))
    if (!hasSameKey) return false

    for (const key of a) if (a[key] !== b[key]) return false

    return true
  }

  const AddPerson = (event) => {
    event.preventDefault()
    const personObj = {
      name: newName,
      num: newNum,
      show: true
    }
    if (person.find(checkName)) {
      alert(`${newName} already add to phonebook`)
      return
    }
    setPerson(person.concat(personObj))
    setNewName('')
  }

  return (
    <div>
      <h2>search</h2>
      <div>
        <form>
          <div>
            search: <input value={newSearch} onChange={HandleSearchChange}/>
          </div>
        </form>
      </div>
      <h2>phonebook</h2>
      <form onSubmit={AddPerson}>
        <div>
          name: <input value={newName} onChange={HandleNameChange}/>
        </div>
        <div>
          num: <input value={newNum} onChange={HandleNumChange}/>
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>numbers</h2>
        <ListPerson person={person}/>
    </div>
  )
}

export default App
